import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchEmployees } from "../features/Slice/employees";

function Home() {
	const dispatch = useDispatch();
	const employees = useSelector((state) => state.employees);
	const today = useSelector((state) => state.date);
	const list = employees.list;
	const status = employees.status;
	const error = employees.error;

	useEffect(() => {
		dispatch(fetchEmployees());
	}, [dispatch]);

	if (status === "loading") {
		return <div>Loading...</div>;
	}

	if (status === "failed") {
		return <div>Error: {error}</div>;
	}

	return (
		<div>
			<h1>Employees :{today}</h1>
			<ul>
				{list.map((employee) => (
					<li key={employee.id}>
						{employee.id}|{employee.firstName}#{employee.lastName}=
						{employee.dateOfBirth}/{employee.department}!{employee.street}
						{employee.city}
						{employee.zipCode}+{employee.state}
					</li>
				))}
			</ul>
		</div>
	);
}

export default Home;
