import React from "react";
import { ImSpinner2, ImBlocked, ImEllo, ImShocked } from "react-icons/im";
import "./style.css";

function StatusIcon({ status }) {
	//"loading"/"failed"/"success"

	let icon = null;
	let color = null;
	let isSpinning = false;

	switch (status) {
		case "loading":
			icon = <ImSpinner2 />;
			color = "ff8c00";
			isSpinning = true;
			break;
		case "failed":
			icon = <ImBlocked />;
			color = "ff4500";
			isSpinning = false;
			break;
		case "success":
			icon = <ImEllo />;
			color = "32cd32";
			isSpinning = false;
			break;
		default:
			icon = <ImShocked />;
			color = "dda0dd";
			isSpinning = true;
	}

	return (
		<div className={isSpinning ? "spinner" : null} style={`color:${color}`}>
			{icon}
		</div>
	);
}

export default StatusIcon;
