import React from "react";
import { ImSpinner2, ImBlocked, ImEllo, ImShocked } from "react-icons/im";
import "./style.css";

function StatusIcon({ status }) {
	//"loading"/"failed"/"success"

	let icon = null;
	let isSpinning = false;

	switch (status) {
		case "loading":
			icon = <ImSpinner2 color="0xff8c00" />;
			isSpinning = true;
			break;
		case "failed":
			icon = <ImBlocked color="0xff4500" />;
			isSpinning = false;
			break;
		case "success":
			icon = <ImEllo color="0x32cd32" />;
			isSpinning = false;
			break;
		default:
			icon = <ImShocked color="0xdda0dd" />;
			isSpinning = true;
	}

	return <div className={isSpinning ? "spinner" : null}>{icon}</div>;
}

export default StatusIcon;
